export function createERC1271signature(signingSafe: string) {
    let signature = '0x';

    // first 20 bytes are r and the next 20 bytes are s
    //address of the contract is encoded into r
    signature += '000000000000000000000000' + signingSafe.replace('0x', '');

    // s is the pointer
    signature +=
        '';
    signature += '00';

    return signature;
}
